import React, { useState, createContext, FC } from 'react'

interface IRestaurant {
  id: Number;
  name: String;
  location: string;
  price_range: Number;
}

interface RestaurantContextType {
  restaurants: IRestaurant[];
  //addRestaurant: (restaurant: IRestaurant) => void;
}

interface Props {
  children: React.ReactNode
}

export const RestaurantsContext = createContext<RestaurantContextType | null>(null);

export const RestaurantsContextProvider: React.FC<Props> = ({ children }) => {
  const [restaurants, setRestaurants] = useState([]);

  return (
      <RestaurantsContext.Provider value={{restaurants}}>
        {children}
      </RestaurantsContext.Provider>
  )
}
