import axios, { AxiosResponse, AxiosInstance } from 'axios'

interface IRestaurant {
  id: Number;
  name: String;
  location: string;
  price_range: Number;
}


abstract class HttpClient {
  instance: AxiosInstance;
  public constructor(baseURL: string){
    this.instance = axios.create({
      baseURL
    });
  }
}

export class ApiRequest extends HttpClient {
  public constructor(){
    super('http://localhost:4000/api/v1/restaurants')
  }

  public getRestaurants = () => this.instance.get<IRestaurant[]>('/')
}



