import React, { useState, createContext, FC } from 'react'

interface IRestaurant {
  id: Number;
  name: String;
  location: string;
  price_range: Number;
}

// export interface RestaurantContextType {
//   restaurants?: IRestaurant;
//   setRestaurants: (restaurants:IRestaurant) => void;
// }

interface Props {
  children: React.ReactNode
}

type RestaurantContextType = {
  context: IRestaurant[];
  setContext: React.Dispatch<React.SetStateAction<any>>
  addRestaurant: React.Dispatch<React.SetStateAction<any>>
} 

const IRestaurantContextState = {
  context: [],
  setContext: () => {},
  addRestaurant: () => {},
}

export const RestaurantsContext = createContext<RestaurantContextType>(IRestaurantContextState);
//export const RestaurantsContext = createContext<RestaurantContextType | null>(null);

export const RestaurantsContextProvider: React.FC<Props> = ({ children }) => {
  const [restaurants, setRestaurants] = useState([] as any);

  const addRestaurant = (restaurant:any) => {
    setRestaurants([...restaurants, restaurant])
  }
  // const providerValue: RestaurantContextType = {
  //   restaurants,
  //   setRestaurants,
  // };

  // return (
  //     <RestaurantsContext.Provider value={providerValue}>
  //       {children}
  //     </RestaurantsContext.Provider>
  // )
  return (
    <RestaurantsContext.Provider value={{restaurants,setRestaurants,addRestaurant}}>
      {children}
    </RestaurantsContext.Provider>
  )
};
